import './App.css';
import video from './video.mp4';
import { Text, View } from 'react-native';
import {useEffect, useState} from "react";
import audio from './music.mp3'

function App() {
    // use Audio constructor to create HTMLAudioElement
    const audioTune = new Audio(audio);

    // variable to play audio in loop
    const [playInLoop, setPlayInLoop] = useState(true);

    // load audio file on component load
    useEffect(() => {
        audioTune.load();
    }, [])

    // set the loop of audio tune
    useEffect(() => {
        audioTune.loop = playInLoop;
    }, [playInLoop])


    let isMusicTurned = false

    // play audio sound
    const playSound = () => {
        if (!isMusicTurned) {
            audioTune.play();
            isMusicTurned = true;
            return;
        }
        isMusicTurned = false;
        audioTune.pause();
    }

  return (
    <div className="App">

        <video className='videoTag' autoPlay loop muted><div style="
    position: absolute;
    width:100%;
    height:100%;
    background:#000;
    opacity:0.5;
"></div>
            <source src={video} type='video/mp4'/>
        </video>

        <button className="button_music" onClick={playSound}>Music</button>
    </div>

  );
}


export default App;
