import './App.css';
import video from './video.mp4';
import {Text, View} from 'react-native';
import {useEffect, useState} from "react";
import audio from './music.mp3'
import 'bootstrap/dist/css/bootstrap.min.css';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome"
import {faFacebook} from "@fortawesome/free"

function App() {

    // use Audio constructor to create HTMLAudioElement
    const audioTune = new Audio(audio);

    // variable to play audio in loop
    const [playInLoop, setPlayInLoop] = useState(true);

    // load audio file on component load
    useEffect(() => {
        audioTune.load();
    }, [])

    // set the loop of audio tune
    useEffect(() => {
        audioTune.loop = playInLoop;
    }, [playInLoop])


    let isMusicTurned = false

    // play audio sound
    const playSound = () => {
        if (!isMusicTurned) {
            audioTune.play();
            isMusicTurned = true;
            return;
        }
        isMusicTurned = false;
        audioTune.pause();
    }

    return (
        <div className="App">

            <div>
                <video className='videoTag' autoPlay loop muted>
                    <source src={video} type='video/mp4'/>
                </video>
            </div>

            <button className="button_music" onClick={playSound}></button>
            <h1 style={{color: "white", font: "Font Awesome"}}>What</h1>
            <i className="fa-brands fa-facebook"></i>
            <FontAwesomeIcon icon="fa-brands fa-facebook" />
        </div>

    );
}


export default App;
