import './App.css';
import video from './video.mp4';
import { Text, View } from 'react-native';
import {useEffect, useState} from "react";
import audio from './music.mp3'

function App() {
    // use Audio constructor to create HTMLAudioElement
    const audioTune = new Audio(audio);

    // variable to play audio in loop
    const [playInLoop, setPlayInLoop] = useState(true);

    // load audio file on component load
    useEffect(() => {
        audioTune.load();
    }, [])

    // set the loop of audio tune
    useEffect(() => {
        audioTune.loop = playInLoop;
    }, [playInLoop])

    // play audio sound
    const playSound = () => {
        audioTune.play();
    }

    // pause audio sound
    const pauseSound = () => {
        audioTune.pause();
    }

  return (
    <div className="App">

        <video className='videoTag' autoPlay loop muted>
            <source src={video} type='video/mp4'/>
        </video>

            <input type="button" className="btn btn-primary mr-2" value="!" onClick={playSound}></input>
            <input type="button" className="btn btn-warning mr-2" value="Pause" onClick={pauseSound}></input>

    </div>

  );
}


export default App;
